package ImpPrograms;

public class OddNumberorNot {
	public static void odd(int number)
	{
		if (number%2==0)
		{
			System.out.println("even num");
		}
		else
		{
			System.out.println("odd num");
		}
		
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		odd(77);
		

	}

}
